tools=new function(){this.randRange=function(a,b){return Math.random()*(b-a)+a};this.randRangeInt=function(a,b){return Math.floor(this.randRange(a,b+1))};this.getRelativeMousePos=function(a,b){var c,d;a.pageX||a.pageY?(c=a.pageX,d=a.pageY):(c=a.clientX+document.body.scrollLeft+document.documentElement.scrollLeft,d=a.clientY+document.body.scrollTop+document.documentElement.scrollTop);c-=b.offsetLeft;d-=b.offsetTop;return{x:c,y:d}};this.Choose=function(a){return a[this.randRangeInt(0,a.length-1)]}};
